/**
 * Main Layout Component
 * Generated by Zhuang
 */
import React, { useEffect, useState } from 'react';
import { Outlet, useNavigate, useLocation } from 'react-router-dom';
import {
  Layout as AntLayout,
  Menu,
  Button,
  Drawer,
  message,
} from 'antd';
import {
  DashboardOutlined,
  HeartOutlined,
  UserOutlined,
  LogoutOutlined,
  MenuOutlined,
  MenuFoldOutlined,
  MenuUnfoldOutlined,
} from '@ant-design/icons';
import { authAPI, userAPI } from '../services/api';
import { clearTokens, getUserIdFromToken } from '../utils/auth';
import MemberSelector from './MemberSelector';
import { useMember } from '../context/MemberContext';

const { Header, Content, Sider } = AntLayout;

const Layout = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [collapsed, setCollapsed] = useState(false);
  const [mobileMenuVisible, setMobileMenuVisible] = useState(false);
  const { selectedMemberId, setSelectedMemberId } = useMember();
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    // Generated by Zhuang: fetch current user for header display
    const loadUser = async () => {
      try {
        const uid = getUserIdFromToken();
        if (!uid) return;
        const resp = await userAPI.getProfile(uid);
        setCurrentUser(resp.data);
      } catch (e) {
        // ignore header user load errors
      }
    };
    loadUser();
  }, []);

  const menuItems = [
    {
      key: '/dashboard',
      icon: <DashboardOutlined />,
      label: '仪表板',
      onClick: () => navigate('/dashboard'),
    },
    {
      key: '/health-records',
      icon: <HeartOutlined />,
      label: '健康记录',
      onClick: () => navigate('/health-records'),
    },
    {
      key: '/members',
      icon: <UserOutlined />,
      label: '成员管理',
      onClick: () => navigate('/members'),
    },
    {
      key: '/profile',
      icon: <UserOutlined />,
      label: '个人信息',
      onClick: () => navigate('/profile'),
    },
  ];

  const handleLogout = async () => {
    try {
      // Backend /logout requires refresh token; authAPI.logout already posts without header override,
      // our axios interceptor will attach access token by default. We explicitly call refresh endpoint first
      // to obtain a fresh pair and then call logout with the refresh token header to satisfy backend requirement.
      const refreshToken = localStorage.getItem('refresh_token');
      if (!refreshToken) {
        throw new Error('Missing refresh token');
      }
      // Call logout with refresh token as Bearer
      await authAPI.logout({
        headers: { Authorization: `Bearer ${refreshToken}` },
      });
      clearTokens();
      message.success('已成功退出登录');
      navigate('/login');
    } catch (error) {
      console.error('Logout error:', error);
      // Even if API call fails, clear tokens and redirect
      clearTokens();
      navigate('/login');
    }
  };

  const siderContent = (
    <>
      <div style={{ 
        height: 64, 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        borderBottom: '1px solid #f0f0f0',
        background: '#fff'
      }}>
        <h3 style={{ margin: 0, color: '#1890ff' }}>健康平台</h3>
      </div>
      <Menu
        theme="light"
        mode="inline"
        selectedKeys={[location.pathname]}
        items={menuItems}
        style={{ borderRight: 0 }}
      />
    </>
  );

  return (
    <AntLayout className="health-platform-layout">
      {/* Desktop Sidebar */}
      <Sider
        collapsible
        collapsed={collapsed}
        onCollapse={setCollapsed}
        breakpoint="lg"
        collapsedWidth="0"
        className="desktop-sider"
        style={{
          overflow: 'auto',
          height: '100vh',
          position: 'fixed',
          left: 0,
          top: 0,
          bottom: 0,
          background: '#fff',
        }}
      >
        {siderContent}
      </Sider>

      {/* Mobile Drawer */}
      <Drawer
        title="健康平台"
        placement="left"
        onClose={() => setMobileMenuVisible(false)}
        open={mobileMenuVisible}
        styles={{ body: { padding: 0 } }}
        className="mobile-drawer"
      >
        <Menu
          mode="inline"
          selectedKeys={[location.pathname]}
          items={menuItems}
          onClick={() => setMobileMenuVisible(false)}
        />
      </Drawer>

      <AntLayout style={{ marginLeft: collapsed ? 0 : 200 }}>
        <Header
          style={{
            padding: '0 24px',
            background: '#fff',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            borderBottom: '1px solid #f0f0f0',
          }}
        >
          <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
            {/* Mobile: open drawer */}
            <Button
              type="text"
              icon={<MenuOutlined />}
              onClick={() => setMobileMenuVisible(true)}
              className="mobile-menu-button"
              style={{ display: 'none' }}
              title="打开菜单"
            />
            {/* Desktop: toggle sider collapse */}
            <Button
              type="text"
              icon={collapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
              onClick={() => setCollapsed(!collapsed)}
              className="desktop-menu-toggle"
              title={collapsed ? '展开菜单' : '收起菜单'}
            />
            <h4 style={{ margin: 0, marginLeft: 16 }}>
              {menuItems.find(item => item.key === location.pathname)?.label || '健康平台'}
            </h4>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
            {/* Current user display */}
            <div data-testid="current-user" style={{ color: '#555', fontSize: 13 }}>
              <UserOutlined style={{ marginRight: 6 }} />
              {currentUser ? `${currentUser.username} (${currentUser.email})` : ''}
            </div>
            <MemberSelector
              value={selectedMemberId}
              onChange={(v) => setSelectedMemberId(v)}
              style={{ minWidth: 180 }}
            />
          <Button
            type="text"
            icon={<LogoutOutlined />}
            onClick={handleLogout}
            style={{ color: '#ff4d4f' }}
          >
            退出登录
          </Button>
          </div>
        </Header>
        <Content style={{ padding: '24px', minHeight: 'calc(100vh - 64px)' }}>
          <Outlet />
        </Content>
      </AntLayout>

  <style>{`
        @media (max-width: 992px) {
          .desktop-sider {
            display: none !important;
          }
          .ant-layout {
            margin-left: 0 !important;
          }
          .mobile-menu-button {
            display: inline-flex !important;
          }
          .desktop-menu-toggle {
            display: none !important;
          }
        }
        @media (min-width: 993px) {
          .mobile-drawer {
            display: none;
          }
          .desktop-menu-toggle {
            display: inline-flex;
          }
        }
  `}</style>
    </AntLayout>
  );
};

export default Layout;
