/**
 * Profile Page Component
 * Generated by Zhuang
 */
import React, { useState, useEffect, useCallback } from 'react';
import {
  Card,
  Form,
  Input,
  InputNumber,
  Select,
  Button,
  Typography,
  Space,
  message,
  Row,
  Col,
  Avatar,
  Divider,
  Tag,
  Modal,
} from 'antd';
import { UserOutlined, MailOutlined, SaveOutlined, WechatOutlined, CheckCircleOutlined } from '@ant-design/icons';
import { userAPI, wechatAPI } from '../services/api';
import { getUserIdFromToken } from '../utils/auth';
import { useTranslation } from 'react-i18next';
import WeChatQRModal from '../components/WeChatQRModal';

const { Title, Text } = Typography;
const { Option } = Select;

const Profile = () => {
  const { t } = useTranslation();
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [userInfo, setUserInfo] = useState(null);
  const [wechatModalVisible, setWechatModalVisible] = useState(false);
  const [bindingWeChat, setBindingWeChat] = useState(false);

  const fetchUserInfo = useCallback(async () => {
    try {
      setLoading(true);
      const userId = getUserIdFromToken();
      if (!userId) {
        message.error(t('profile.messages.fetchFail'));
        return;
      }

      const response = await userAPI.getProfile(userId);
      const user = response.data;
      setUserInfo(user);
      
      // Set form values
      form.setFieldsValue({
        username: user.username,
        email: user.email,
        age: user.age,
        gender: user.gender,
  height: user.height,
        weight: user.weight,
      });
    } catch (error) {
      console.error('Failed to fetch user info:', error);
  message.error(t('profile.messages.fetchFail'));
    } finally {
      setLoading(false);
    }
  }, [form, t]); // Generated by Zhuang

  useEffect(() => {
    fetchUserInfo();
  }, [fetchUserInfo]);

  const handleSubmit = async (values) => {
    try {
      setSaving(true);
      const userId = getUserIdFromToken();
      if (!userId) {
        message.error(t('profile.messages.fetchFail'));
        return;
      }

      await userAPI.updateProfile(userId, values);
      message.success(t('profile.messages.updateSuccess'));
      
      // Refresh user info
      await fetchUserInfo();
    } catch (error) {
      console.error('Failed to update profile:', error);
  const errorMessage = error.response?.data?.message || t('profile.messages.updateFail');
      message.error(errorMessage);
    } finally {
      setSaving(false);
    }
  };

  const getGenderText = (gender) => {
    switch (gender) {
      case 'M': return t('profile.gender.M');
      case 'F': return t('profile.gender.F');
      case 'O': return t('profile.gender.O');
      default: return t('profile.notSet');
    }
  };

  const handleWeChatBind = () => {
    setWechatModalVisible(true);
  };

  const handleWeChatBindSuccess = async (code) => {
    setBindingWeChat(true);
    try {
      await wechatAPI.bind(code);
      message.success(t('messages.wechat.bindSuccess'));
      setWechatModalVisible(false);
      // Refresh user info
      await fetchUserInfo();
    } catch (error) {
      console.error('Failed to bind WeChat:', error);
      const errorMessage = error.response?.data?.message || t('messages.wechat.bindFail');
      message.error(errorMessage);
    } finally {
      setBindingWeChat(false);
    }
  };

  if (loading) {
    return (
      <Card loading={loading}>
        <div style={{ height: 400 }} />
      </Card>
    );
  }

  return (
    <div>
      <div style={{ marginBottom: 24 }}>
  <Title level={2}>{t('profile.title')}</Title>
      </div>

      <Row gutter={24}>
        <Col xs={24} lg={8}>
          <Card title={t('profile.card.basic')} style={{ marginBottom: 24 }}>
            <div style={{ textAlign: 'center', marginBottom: 24 }}>
              <Avatar size={80} icon={<UserOutlined />} />
              <div style={{ marginTop: 16 }}>
                <Title level={4} style={{ marginBottom: 4 }}>
                  {userInfo?.username}
                </Title>
                <Text type="secondary">{userInfo?.email}</Text>
              </div>
            </div>
            
            <Divider />
            
            <Space direction="vertical" style={{ width: '100%' }}>
              <div>
                <Text strong>{t('profile.username')}：</Text>
                <Text>{userInfo?.username}</Text>
              </div>
              <div>
                <Text strong>{t('profile.email')}：</Text>
                <Text>{userInfo?.email}</Text>
              </div>
              <div>
                <Text strong>{t('profile.age')}：</Text>
                <Text>{userInfo?.age || t('profile.notSet')}</Text>
              </div>
              <div>
                <Text strong>{t('profile.gender')}：</Text>
                <Text>{getGenderText(userInfo?.gender)}</Text>
              </div>
              <div>
                <Text strong>{t('profile.height').split(' (')[0]}：</Text>
                <Text>{userInfo?.height ? `${userInfo.height} cm` : t('profile.notSet')}</Text>
              </div>
              <div>
                <Text strong>{t('profile.weight').split(' (')[0]}：</Text>
                <Text>{userInfo?.weight ? `${userInfo.weight} kg` : t('profile.notSet')}</Text>
              </div>
              <div>
                <Text strong>{t('profile.createdAt')}：</Text>
                <Text>
                  {userInfo?.created_at ? 
                    new Date(userInfo.created_at).toLocaleDateString() : 
                    t('profile.unknown')
                  }
                </Text>
              </div>
              
              <Divider />
              
              <div>
                <Space direction="vertical" style={{ width: '100%' }}>
                  <Text strong>{t('profile.wechat.title')}：</Text>
                  {userInfo?.wechat_unionid ? (
                    <Tag icon={<CheckCircleOutlined />} color="success">
                      {t('profile.wechat.bound')}
                    </Tag>
                  ) : (
                    <>
                      <Text type="secondary">{t('profile.wechat.notBound')}</Text>
                      <Button
                        type="primary"
                        icon={<WechatOutlined />}
                        onClick={handleWeChatBind}
                        loading={bindingWeChat}
                        style={{
                          backgroundColor: '#07C160',
                          borderColor: '#07C160',
                        }}
                      >
                        {t('profile.wechat.bind')}
                      </Button>
                    </>
                  )}
                </Space>
              </div>
            </Space>
          </Card>
        </Col>

        <Col xs={24} lg={16}>
          <Card title={t('profile.card.edit')}>
            <Form
              form={form}
              layout="vertical"
              onFinish={handleSubmit}
            >
              <Row gutter={16}>
                <Col span={24}>
                  <Form.Item
                    name="username"
                    label={t('profile.username')}
                    rules={[
                      { required: true, message: t('profile.username') },
                      { min: 2, message: t('profile.username') }
                    ]}
                  >
                    <Input
                      prefix={<UserOutlined />}
                      placeholder={t('profile.username')}
                    />
                  </Form.Item>
                </Col>
              </Row>

              <Row gutter={16}>
                <Col span={24}>
                  <Form.Item
                    name="email"
                    label={t('profile.email')}
                    rules={[
                      { required: true, message: t('profile.email') },
                      { type: 'email', message: t('profile.email') }
                    ]}
                  >
                    <Input
                      prefix={<MailOutlined />}
                      placeholder={t('profile.email')}
                      disabled // Email should not be editable to maintain auth integrity
                    />
                  </Form.Item>
                </Col>
              </Row>

              <Row gutter={16}>
                <Col xs={24} sm={12}>
                  <Form.Item
                    name="age"
                    label={t('profile.age')}
                    rules={[
                      { type: 'number', min: 1, max: 120, message: '' }
                    ]}
                  >
                    <InputNumber
                      placeholder={t('profile.age')}
                      style={{ width: '100%' }}
                      min={1}
                      max={120}
                    />
                  </Form.Item>
                </Col>

                <Col xs={24} sm={12}>
                  <Form.Item
                    name="gender"
                    label={t('profile.gender')}
                  >
                    <Select placeholder={t('profile.gender')}>
                      <Option value="M">{t('profile.gender.M')}</Option>
                      <Option value="F">{t('profile.gender.F')}</Option>
                      <Option value="O">{t('profile.gender.O')}</Option>
                    </Select>
                  </Form.Item>
                </Col>
              </Row>

              <Row gutter={16}>
                <Col xs={24} sm={12}>
                  <Form.Item
                    name="height"
                    label={t('profile.height')}
                    rules={[
                      { type: 'number', min: 1, max: 300, message: '' }
                    ]}
                  >
                    <InputNumber
                      placeholder={t('profile.height')}
                      style={{ width: '100%' }}
                      min={1}
                      max={300}
                      step={0.1}
                    />
                  </Form.Item>
                </Col>
              </Row>

              <Row gutter={16}>
                <Col xs={24} sm={12}>
                  <Form.Item
                    name="weight"
                    label={t('profile.weight')}
                    rules={[
                      { type: 'number', min: 1, max: 500, message: '' }
                    ]}
                  >
                    <InputNumber
                      placeholder={t('profile.weight')}
                      style={{ width: '100%' }}
                      min={1}
                      max={500}
                      step={0.1}
                    />
                  </Form.Item>
                </Col>
              </Row>

              <Form.Item>
                <Button
                  type="primary"
                  htmlType="submit"
                  loading={saving}
                  icon={<SaveOutlined />}
                  size="large"
                >
                  {t('profile.save')}
                </Button>
              </Form.Item>
            </Form>
          </Card>
        </Col>
      </Row>

      <WeChatQRModal
        visible={wechatModalVisible}
        onClose={() => setWechatModalVisible(false)}
        onSuccess={handleWeChatBindSuccess}
      />
    </div>
  );
};

export default Profile;
